import React from 'react';

const Product = ({ product, onEdit, onDelete }) => {
  const { description, canExpire, expiryDate, category, price, isOnSpecial } = product;

  const handleEdit = () => {
    onEdit(product);
  };

  const handleDelete = () => {
    onDelete(product);
  };

  return (
    <div className={`product ${isOnSpecial ? 'special' : ''}`}>
      <h3>{description}</h3>
      <p>Category: {category}</p>
      <p>Price: ${price}</p>
      {canExpire && <p>Expiry Date: {expiryDate}</p>}
      <p>{isOnSpecial ? 'On Special!' : ''}</p>
      <button onClick={handleEdit}>Edit</button>
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
};

export default Product;
