import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';

const ProductForm = ({ onSave }) => {
  const [formData, setFormData] = useState({
    description: '',
    canExpire: false,
    expiryDate: '',
    category: '',
    price: '',
    isOnSpecial: false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
    setFormData({
      description: '',
      canExpire: false,
      expiryDate: '',
      category: '',
      price: '',
      isOnSpecial: false,
    });
  };

  return (
    <div>
      <h2>Add New Product</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Description:</Form.Label>
        <Form.Control as="textarea" rows={3} name="description" value={formData.description} onChange={handleChange} />

          {/* <Form.Control type="text" name="description" value={formData.description} onChange={handleChange} /> */}
        </Form.Group>
        <Form.Group>
          <Form.Check
            type="checkbox"
            label="Can Expire"
            name="canExpire"
            checked={formData.canExpire}
            onChange={(e) => setFormData({ ...formData, canExpire: e.target.checked })}
          />
        </Form.Group>
        {formData.canExpire && (
          <Form.Group>
            <Form.Label>Expiry Date:</Form.Label>
            <Form.Control type="date" name="expiryDate" value={formData.expiryDate} onChange={handleChange} />
          </Form.Group>
        )}
        <Form.Group>
          <Form.Label>Category:</Form.Label>
          <select name="category" value={formData.category} onChange={handleChange} >
            <option value="grapefruit">Grapefruit</option>
            <option value="lime">Lime</option>
            <option selected value="coconut">Coconut</option>
            <option value="mango">Mango</option>
            </select>
          {/* <Form.Control type="text" name="category" value={formData.category} onChange={handleChange} /> */}
        </Form.Group>
        <Form.Group>
          <Form.Label>Price:</Form.Label>
          <Form.Control type="number" name="price" value={formData.price} onChange={handleChange} />
        </Form.Group>
        <Form.Group>
          <Form.Check
            type="checkbox"
            label="On Special"
            name="isOnSpecial"
            checked={formData.isOnSpecial}
            onChange={(e) => setFormData({ ...formData, isOnSpecial: e.target.checked })}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Save Product
        </Button>
      </Form>
      <hr/>
    </div>
  );
};

export default ProductForm;
