import React, { useState, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';

const ProductForm = ({ onSave, editingProduct }) => {
  const [formData, setFormData] = useState({
    description: '',
    canExpire: false,
    expiryDate: '',
    category: '',
    price: '',
    isOnSpecial: false,
  });

  // Update form data when editingProduct changes
  useEffect(() => {
    if (editingProduct) {
      setFormData({
        description: editingProduct.description,
        canExpire: editingProduct.canExpire,
        expiryDate: editingProduct.expiryDate,
        category: editingProduct.category,
        price: editingProduct.price,
        isOnSpecial: editingProduct.isOnSpecial,
      });
    } else {
      setFormData({
        description: '',
        canExpire: false,
        expiryDate: '',
        category: '',
        price: '',
        isOnSpecial: false,
      });
    }
  }, [editingProduct]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : value;
    setFormData({ ...formData, [name]: newValue });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingProduct) {
      onSave({ ...formData, id: editingProduct.id });
    } else {
      onSave({ ...formData, id: Math.random() }); // Generate unique ID (not suitable for production)
    }
    setFormData({
      description: '',
      canExpire: false,
      expiryDate: '',
      category: '',
      price: '',
      isOnSpecial: false,
    });
  };

  return (
    <div>
      <h2>{editingProduct ? 'Edit Product' : 'Add New Product'}</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Description:</Form.Label>
          <Form.Control type="text" name="description" value={formData.description} onChange={handleChange} />
        </Form.Group>
        <Form.Group>
          <Form.Check
            type="checkbox"
            label="Can Expire"
            name="canExpire"
            checked={formData.canExpire}
            onChange={handleChange}
          />
        </Form.Group>
        {formData.canExpire && (
          <Form.Group>
            <Form.Label>Expiry Date:</Form.Label>
            <Form.Control type="date" name="expiryDate" value={formData.expiryDate} onChange={handleChange} />
          </Form.Group>
        )}
        <Form.Group>
          <Form.Label>Category:</Form.Label>
          <Form.Control type="text" name="category" value={formData.category} onChange={handleChange} />
        </Form.Group>
        <Form.Group>
          <Form.Label>Price:</Form.Label>
          <Form.Control type="number" name="price" value={formData.price} onChange={handleChange} />
        </Form.Group>
        <Form.Group>
          <Form.Check
            type="checkbox"
            label="On Special"
            name="isOnSpecial"
            checked={formData.isOnSpecial}
            onChange={handleChange}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          {editingProduct ? 'Save Changes' : 'Save'}
        </Button>
      </Form>
    </div>
  );
};

export default ProductForm;
