import React, { useState } from 'react';
import { ListGroup, Button, Form } from 'react-bootstrap';

const ProductList = ({ products, onEdit, onDelete }) => {
  const [selectedCategory, setSelectedCategory] = useState('All');

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
  };

  const filteredProducts = selectedCategory === 'All' ? products : products.filter(p => p.category === selectedCategory);

  return (
    <div>
        <hr/>
      <h2>Product List</h2>
      <Form.Group>
        <Form.Label>Filter by Category:</Form.Label>
        <Form.Control as="select" value={selectedCategory} onChange={handleCategoryChange}>
          <option value="All">All</option>
          <option value="Vegetables">Vegetables</option>
          <option value="Meat">Meat</option>
          <option value="Furniture">Furniture</option>
          {/* Add more options for other categories */}
        </Form.Control>
      </Form.Group>
      <ListGroup>
        {filteredProducts.map((product) => (
          <ListGroup.Item key={product.id} className={product.isOnSpecial ? 'special' : ''}>
            <strong>{product.description}</strong>
            <p>Category: {product.category}</p>
            <p>Price: ${product.price}</p>
            {product.canExpire && <p>Expiry Date: {product.expiryDate}</p>}
            <p>{product.isOnSpecial ? 'On Special!' : 'Regular Price'}</p>
            <Button variant="primary" onClick={() => onEdit(product)}>
              Edit
            </Button>{' '}
            <Button variant="danger" onClick={() => onDelete(product.id)}>
              Delete
            </Button>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
};

export default ProductList;
